    def crear_base(self):
        cursor = conn.cursor()
        cadena_sql = 'CREATE TABLE IF NOT EXISTS Equipo (nombre TEXT, siglas TEXT, estadio TEXT, seguidores INTEGER, campeonatos INTEGER)'
        cursor.execute(cadena_sql)
        cursor.close()

    def guardar_informacion(self):
        cursor = conn.cursor()
        nombre = self.nombre.text()
        siglas = self.siglas.text()
        estadio = self.estadio.text()
        seguidores = int(self.seguidores.text())
        campeonatos = int(self.campeonatos.text())
        cadena_sql = """INSERT INTO Equipo (nombre, siglas, estadio, seguidores, campeonatos) VALUES (?, ?, ?, ?, ?)"""
        cursor.execute(cadena_sql, (nombre, siglas, estadio, seguidores, campeonatos))
        conn.commit()
        cursor.close()
        self.obtener_informacion()

    def obtener_informacion(self):
        cursor = conn.cursor()
        cadena_consulta_sql = "SELECT * from Equipo"
        cursor.execute(cadena_consulta_sql)
        informacion = cursor.fetchall()
        self.listaEquipos.setRowCount(len(informacion))
        for i, fila in enumerate(informacion):
            for j, valor in enumerate(fila):
                self.listaEquipos.setItem(i, j, QtWidgets.QTableWidgetItem(str(valor)))
        cursor.close()

